import MarkdownIt from 'markdown-it';

interface CSSI18nOptions {
    /**
     * Languages to support
     * @default ['zh-CN', 'en']
     */
    languages?: string[];
}
/**
 * Add a container to markdown-it to support i18n
 * @example
 * ```markdown
 * ::: zh-CN
 * 你好
 * :::
 *
 * ::: en
 * Hello
 * :::
 * ```
 * @param md
 * @param options
 */
declare function cssI18nContainer(md: MarkdownIt, options?: CSSI18nOptions): void;

export { type CSSI18nOptions, cssI18nContainer };
